#!/bin/python# -*- coding:utf-8 -*-#Convert XDATCAR to PDB and extract energy &amp; temperature profile for AIMD simulations# -h for help;-b for frame started;-e for frame ended;;-p for PDB conversion  #by nxu tamas@zju.edu.cn#version 1.2#date 2019.4.9import osimport shutilimport numpy as npimport matplotlib as mplimport mathmpl.use(&#x27;Agg&#x27;) #silent modefrom matplotlib import pyplot as pltfrom optparse import OptionParser  import sysfrom copy import deepcopyclass debug(object):    def __init__(self,info=&#x27;debug&#x27;):        self.info=info    def __call__(self,func):        def wrapper(*args,**kwargs):            print(&#x27;[&#123;info&#125;] Now entering function &#123;function&#125;.....&#x27; \                .format(info=self.info,function=getattr(func,&quot;__name__&quot;)))            return func(*args,**kwargs)        return wrapperclass Energy_Temp(object):    def __init__(self):        &#x27;Read vasp MD energies and temperature.&#x27;        self.temp=[]        self.energy=[]        self.energy_extract()                def energy_extract(self):        print(&#x27;Now reading vasp MD energies and temperature.&#x27;)                if os.path.exists(&quot;OSZICAR&quot;):            oszicar=open(&quot;OSZICAR&quot;,&#x27;r&#x27;)            for index,line in enumerate(oszicar):                if &quot;E0=&quot; in line:                    self.temp.append(float(line.split()[2]))                    self.energy.append(float(line.split()[4]))            oszicar.close()            #return (self.temp,self.energy)                                 else:            raise IOError(&#x27;OSZICAR does not exist!&#x27;)class XDATCAR(Energy_Temp):    def __init__(self):        &#x27;Read lattice information and atomic coordinate.&#x27;        super(XDATCAR,self).__init__()        print(&#x27;Now reading vasp XDATCAR.&#x27;)               self.lattice=np.zeros((3,3))        self.NPT=False        self.frames=0        self._timestep=1  # timestep 1fs        self.alpha=0.0        self.beta=0.0        self.gamma=0.0        self.current_frame=0                self._lattice1=0.0        self._lattice2=0.0        self._lattice3=0.0        self._lattice=np.zeros(3)        self.format_trans=False        @property         def timestep(self):            return self._timestep        @timestep.setter        def timestep(self,new_time_step):            self._timestep=new_time_step                    if os.path.exists(&quot;XDATCAR&quot;):            self.XDATCAR=open(&quot;XDATCAR&quot;,&#x27;r&#x27;)        else:            raise IOError(&#x27;XDATCAR does not exist!&#x27;)        title=self.XDATCAR.readline().strip()        for index,line in enumerate(self.XDATCAR):            if &quot;Direct&quot; in line:                self.frames+=1            # elif title in line:            #     self.NPT=True        self.XDATCAR.seek(0)        self.lattice_read()        self.XDATCAR.readline()        for i in range(self.total_atom):            self.XDATCAR.readline()        if &quot;Direct configuration&quot; not in self.XDATCAR.readline():        	self.NPT=True	        #self.frames=len([&#x27;Direct&#x27; for line in self.XDATCAR if &quot;Direct&quot; in line])        print(&#x27;Total frames &#123;0&#125;, NpT is &#123;1&#125;&#x27;.format(self.frames,self.NPT))        # assert len(self.energy)==self.frames, \        #     &#x27;Number of XDATCAR frames does not equal to that of Energy terms in OSZICAR&#x27;           self.XDATCAR.seek(0)        self.lowrange=0;self.uprange=self.frames-1        if self.NPT == False: self.lattice_read()            def step_select(self,selected_step): # &#x27;t&gt;100ps and t &lt; 1000ps&#x27;        &#x27;eg. t &gt; 100 and t &lt; 1000&#x27;        assert isinstance(selected_step,str),&#x27;Selected timestep must be in a &quot;string&quot;&#x27;        if &#x27;and&#x27; in selected_step:            conditions=selected_step.split(&quot;and&quot;)        else:            conditions=[selected_step]        for condition in conditions:            try:                if &#x27;&gt;=&#x27; in condition:                    ranges=int(condition.split(&#x27;&gt;=&#x27;)[1].strip())                    self.lowrange=ranges-1                elif &#x27;&lt;=&#x27; in condition:                    ranges=int(condition.split(&#x27;&lt;=&#x27;)[1].strip())                    self.uprange=ranges-1                elif &#x27;&gt;&#x27; in condition:                    ranges=int(condition.split(&#x27;&gt;&#x27;)[1].strip())                    self.lowrange=ranges                elif &#x27;&lt;&#x27; in condition:                    ranges=int(condition.split(&#x27;&lt;&#x27;)[1].strip())                    self.uprange=ranges-2                else:                    print(&#x27;Selected timestep is invaid!&#x27;);continue            except ValueError:                print(&#x27;Selected timestep is invaid!&#x27;)                 sys.exit(0)            if (self.lowrange &gt;= self.frames-1) or (self.uprange &lt; 0):                raise ValueError(&#x27;Selected timestep is wrong!&#x27;)            if self.lowrange &lt; 0: self.lowrange= 0            if self.uprange &gt; self.frames-1: self.uprange= self.frames-1                              def lattice_read(self):            self.title=self.XDATCAR.readline().rstrip(&#x27;\r\n&#x27;).rstrip(&#x27;\n&#x27;)        self.scaling_factor=float(self.XDATCAR.readline())        for i in range(3):            self.lattice[i]=np.array([float(j) for j in self.XDATCAR.readline().split()])        self.lattice*=self.scaling_factor        self._lattice1=np.sqrt(np.sum(np.square(self.lattice[0])))        self._lattice2=np.sqrt(np.sum(np.square(self.lattice[1])))        self._lattice3=np.sqrt(np.sum(np.square(self.lattice[2])))        self._lattice[0]=self._lattice1        self._lattice[1]=self._lattice2        self._lattice[2]=self._lattice3        self.alpha=math.acos(np.dot(self.lattice[1],self.lattice[2]) \            /float((self._lattice2*self._lattice3)))/np.pi*180.0        self.beta=math.acos(np.dot(self.lattice[0],self.lattice[2]) \            /float((self._lattice1*self._lattice3)))/np.pi*180.0        self.gamma=math.acos(np.dot(self.lattice[0],self.lattice[1]) \            /float((self._lattice1*self._lattice2)))/np.pi*180.0        self.element_list=[j for j in self.XDATCAR.readline().split()]        try:            self.element_amount=[int(j) for j in self.XDATCAR.readline().split()]        except ValueError:            raise ValueError(&#x27;VASP 5.x XDATCAR is needed!&#x27;)        self.total_elements=[]        for i in range(len(self.element_amount)):            self.total_elements.extend([self.element_list[i]]*self.element_amount[i])        #print(self.total_elements)        self.total_atom=sum(self.element_amount)        self.atomic_position=np.zeros((self.total_atom,3))    def __iter__(self):        return self    def __next__(self):        self.next()    def skiplines_(self):        if self.NPT == True: self.lattice_read()         self.XDATCAR.readline()        for i in range(self.total_atom):            self.XDATCAR.readline()    def next(self):        if self.NPT == True: self.lattice_read()         self.XDATCAR.readline()        for i in range(self.total_atom):            line_tmp=self.XDATCAR.readline()            self.atomic_position[i]=np.array([float(j) for j in line_tmp.split()[0:3]])                 self.cartesian_position=np.dot(self.atomic_position,self.lattice)        self.current_frame+=1        #        #self.cartesian_position*=self.scaling_factor        return self.cartesian_position    def writepdb(self,pdb_frame):        tobewriten=[]        tobewriten.append(&quot;MODEL         %r&quot; %(pdb_frame))        tobewriten.append(&quot;REMARK   Converted from XDATCAR file&quot;)        tobewriten.append(&quot;REMARK   Converted using VASPKIT&quot;)        tobewriten.append(&#x27;CRYST1&#123;0:9.3f&#125;&#123;1:9.3f&#125;&#123;2:9.3f&#125;&#123;3:7.2f&#125;&#123;4:7.2f&#125;&#123;5:7.2f&#125;&#x27; .format(self._lattice1,\            self._lattice2,self._lattice3,self.alpha,self.beta,self.gamma))        #print(len(self.total_elements))        for i in range(len(self.total_elements)):            tobewriten.append(&#x27;%4s%7d%4s%5s%6d%4s%8.3f%8.3f%8.3f%6.2f%6.2f%12s&#x27; \                %(&quot;ATOM&quot;,i+1,self.total_elements[i],&quot;MOL&quot;,1,&#x27;    &#x27;,self.cartesian_position[i][0],\                self.cartesian_position[i][1],self.cartesian_position[i][2],1.0,0.0,self.total_elements[i]))        tobewriten.append(&#x27;TER&#x27;)        tobewriten.append(&#x27;ENDMDL&#x27;)        with open(&#x27;XDATCAR.pdb&#x27;,&#x27;a+&#x27;) as pdbwriter:            tobewriten=[i+&#x27;\n&#x27; for i in tobewriten]            pdbwriter.writelines(tobewriten)    def unswrapPBC(self,prev_atomic_cartesian):        diff= self.cartesian_position-prev_atomic_cartesian        prev_atomic_cartesian=deepcopy(self.cartesian_position)         xx=np.where(diff[:,0]&gt;(self._lattice1/2),diff[:,0]-self._lattice1\            ,np.where(diff[:,0]&lt;-(self._lattice1/2),diff[:,0]+self._lattice1\                ,diff[:,0]))        yy=np.where(diff[:,1]&gt;(self._lattice2/2),diff[:,1]-self._lattice2\            ,np.where(diff[:,1]&lt;-(self._lattice2/2),diff[:,1]+self._lattice2\                ,diff[:,1]))        zz=np.where(diff[:,2]&gt;(self._lattice3/2),diff[:,2]-self._lattice3\            ,np.where(diff[:,2]&lt;-(self._lattice3/2),diff[:,2]+self._lattice3\                ,diff[:,2]))        xx=xx.reshape(-1,1);yy=yy.reshape(-1,1);zz=zz.reshape(-1,1)        return (prev_atomic_cartesian,np.concatenate([xx,yy,zz],axis=1))    def reset_cartesian(self,real_atomic_cartesian,center_atom):        if center_atom &gt; len(real_atomic_cartesian)-1:            raise SystemError(&quot;Selected atom does not exist!&quot;)        for i in range(0,len(real_atomic_cartesian)):            for j in range(3):                if (real_atomic_cartesian[i][j]-real_atomic_cartesian[center_atom][j])&gt;self._lattice[j]/2:                    real_atomic_cartesian[i][j]-=self._lattice[j]                if (real_atomic_cartesian[i][j]-real_atomic_cartesian[center_atom][j])&lt;-self._lattice[j]/2:                    real_atomic_cartesian[i][j]+=self._lattice[j]                        return real_atomic_cartesian                        def __call__(self,selected_step):        self.step_select(selected_step)    def __str__(self):        return (&#x27;Read lattice information and atomic coordinate.&#x27;)    __repr__=__str__    class plot(object):    &#x27;Plot MD temperature and energy profile&#x27;    def __init__(self,lwd,font,dpi,figsize,XDATCAR_inst=None):        self.XDATCAR_inst=XDATCAR_inst;self.timestep=self.XDATCAR_inst.timestep        self.time_range=(self.XDATCAR_inst.lowrange,self.XDATCAR_inst.uprange+1)        self.lwd=lwd;self.font=font;self.dpi=dpi;self.figsize=figsize    #@debug(info=&#x27;debug&#x27;)    def plotfigure(self,title=&#x27;MD temperature and energy profile&#x27;):        from matplotlib import pyplot as plt        self.newtemp=self.XDATCAR_inst.temp;self.newenergy=self.XDATCAR_inst.energy        xdata=np.arange(self.time_range[0],self.time_range[1])*self.timestep        axe = plt.subplot(121)        self.newtemp=self.newtemp[self.time_range[0]:self.time_range[1]]        axe.plot(xdata,self.newtemp, \                 color=&#x27;black&#x27;, lw=self.lwd, linestyle=&#x27;-&#x27;, alpha=1)        with open(&quot;Temperature.dat&quot;,&#x27;w&#x27;) as writer:            writer.write(&quot;Time(fs)   Temperature(K)\n&quot;)            for line in range(len(xdata)):                writer.write(&quot;&#123;0:f&#125;  &#123;1:f&#125;\n&quot; .format(xdata[line],self.newtemp[line]))        axe.set_xlabel(r&#x27;$Time$ (fs)&#x27;,fontdict=self.font)        axe.set_ylabel(r&#x27;$Temperature$ (K)&#x27;,fontdict=self.font)        axe.set_xlim((self.time_range[0]*self.timestep, self.time_range[1]*self.timestep))        axe.set_title(&#x27;MD temperature profile&#x27;)                axe1 = plt.subplot(122)        self.newenergy=self.newenergy[self.time_range[0]:self.time_range[1]]        axe1.plot(xdata,self.newenergy, \                 color=&#x27;black&#x27;, lw=self.lwd, linestyle=&#x27;-&#x27;, alpha=1)        with open(&quot;Energy.dat&quot;,&#x27;w&#x27;) as writer:            writer.write(&quot;Time(fs)   Energy(ev)\n&quot;)            for line in range(len(xdata)):                writer.write(&quot;&#123;0:f&#125;  &#123;1:f&#125;\n&quot; .format(xdata[line],self.newenergy[line]))                axe1.set_xlabel(r&#x27;$Time$ (fs)&#x27;,fontdict=self.font)        axe1.set_ylabel(r&#x27;$Energy$ (ev)&#x27;,fontdict=self.font)        axe1.set_xlim((self.time_range[0]*self.timestep, self.time_range[1]*self.timestep))        axe1.set_title(&#x27;MD energy profile&#x27;)        #plt.suptitle(title)        fig = plt.gcf()        fig.set_size_inches(self.figsize)        plt.tight_layout()         plt.savefig(&#x27;ENERGY.png&#x27;,bbox_inches=&#x27;tight&#x27;,pad_inches=0.1,dpi=self.dpi)                if __name__ == &quot;__main__&quot;:    parser = OptionParser()      parser.add_option(&quot;-b&quot;, &quot;--begin&quot;,                         dest=&quot;begin&quot;, default=1,                        help=&quot;frames begin&quot;)     parser.add_option(&quot;-e&quot;, &quot;--end&quot;,                        dest=&quot;end&quot;, default=&#x27;false&#x27;,                        help=&quot;frames end&quot;)     parser.add_option(&quot;-t&quot;, &quot;--timestep&quot;,                        dest=&quot;timestep&quot;, default=1.0,                        help=&quot;timestep per frame&quot;)     parser.add_option(&quot;-p&quot;, &quot;--pdb&quot;,                        action=&quot;store_true&quot;, dest=&quot;format_trans&quot;, default=False,                        help=&quot;choose whether to convert XDATCAR to PDB!&quot;)     parser.add_option(&quot;--pbc&quot;,                        action=&quot;store_true&quot;, dest=&quot;periodic&quot;, default=False,                        help=&quot;choose whether to swrap PBC images!&quot;)     parser.add_option(&quot;-i&quot;, &quot;--index&quot;,                       dest=&quot;index&quot;, default=-1,                        help=&quot;choose which atom to center whole molecule!&quot;)     parser.add_option(&quot;--interval&quot;,                       dest=&quot;interval&quot;, default=1,                        help=&quot;extract frames interval!&quot;)     (options,args) = parser.parse_args()    try:        float(options.timestep)        int(options.index)        _interval=int(options.interval)        int(options.begin)        if options.end != &#x27;false&#x27;: int(options.end)    except:        raise ValueError(&#x27;wrong arguments&#x27;)    XDATCAR_inst=XDATCAR()    XDATCAR_iter=iter(XDATCAR_inst)    if options.format_trans:        XDATCAR_inst.format_trans=True        if os.path.exists(&quot;XDATCAR.pdb&quot;):            shutil.copyfile(&quot;XDATCAR.pdb&quot;,&quot;XDATCAR-bak.pdb&quot;)            os.remove(&quot;XDATCAR.pdb&quot;)    else:        XDATCAR_inst.format_trans=False    XDATCAR_inst.timestep=float(options.timestep)   #timestep 1fs    if options.end == &#x27;false&#x27;:        XDATCAR_inst(&#x27;t&gt;= %r&#x27; %(int(options.begin)))    else:        XDATCAR_inst(&#x27;t&gt;= %r and t &lt;= %r&#x27; %(int(options.begin),int(options.end))) # frame 10~300  corresponding to 20~600fs    if _interval &gt;(XDATCAR_inst.uprange-XDATCAR_inst.lowrange):        raise SystemError(&#x27;Trajectory interval exceed selected time range!&#x27;)    elif _interval&lt;=1:        _interval=1    count=0     current_pdb=1      for i in range(XDATCAR_inst.uprange+1):         if (i&gt;=XDATCAR_inst.lowrange):            cartesian_position=XDATCAR_iter.next()            if count % _interval == 0:                if options.format_trans == True:                     if options.periodic == True:             