from decimal import Decimal, getcontextimport refrom sympy import symbols, Matrix, linsolve, Rationalfrom collections import defaultdict# Improve Decimal precisiongetcontext().prec = 100class FormulaParser:    @staticmethod    def parse(formula: str) -&gt; dict:        elements = &#123;&#125;        pattern = re.compile(r&quot;([A-Z][a-z]?)([\d.]*)?&quot;)        idx = 0        while idx &lt; len(formula):            m = pattern.match(formula, idx)            if not m:                raise ValueError(f&quot;Invalid formula at: &#123;formula[idx:]&#125;&quot;)            elem, num = m.group(1), m.group(2)            coeff = Decimal(num) if num else Decimal(1)            elements[elem] = elements.get(elem, Decimal(0)) + coeff            idx = m.end()        return elementsclass AtomicMassReader:    @staticmethod    def read(filename=&quot;atomic_masses.txt&quot;) -&gt; dict:        masses = &#123;&#125;        try:            with open(filename, &#x27;r&#x27;) as f:                for line in f:                    line = line.strip()                    if not line or line.startswith(&#x27;#&#x27;):                        continue                    parts = line.split()                    if len(parts) != 2:                        raise ValueError(f&quot;Line format error: &#123;line&#125;&quot;)                    masses[parts[0]] = Decimal(parts[1])        except FileNotFoundError:            raise FileNotFoundError(f&quot;File &#123;filename&#125; not found&quot;)        if not masses:            raise ValueError(&quot;Atomic masses file empty&quot;)        return massesclass SynthesisCalculator:    def __init__(self, atomic_masses: dict):        self.atomic_masses = atomic_masses        self.target = &#123;&#125;        self.target_mm = Decimal(0)        self.reagents = []        self.reagent_formulas = []        self.molar_masses = []    def input_target(self):        f = input(&quot;Enter target formula: &quot;).strip()        self.target = FormulaParser.parse(f)        for e, c in self.target.items():            if e not in self.atomic_masses:                raise ValueError(f&quot;Mass of element &#123;e&#125; not defined&quot;)            self.target_mm += c * self.atomic_masses[e]        print(f&quot;Parsed: &#123;self.target&#125;&quot;)        print(f&quot;Target molar mass = &#123;self.target_mm.normalize()&#125; g/mol&quot;)    def input_reagents(self):        n = int(input(&quot;Enter number of reagents: &quot;))        for i in range(n):            f = input(f&quot;Reagent &#123;i+1&#125; formula: &quot;).strip()            self.reagent_formulas.append(f)            parsed = FormulaParser.parse(f)            mm = sum(parsed[e] * self.atomic_masses[e] for e in parsed)            self.reagents.append(parsed)            self.molar_masses.append(mm)            print(f&quot;&#123;f&#125; molar mass = &#123;mm.normalize()&#125; g/mol&quot;)    def validate(self):        tgt = set(self.target)        prov = set().union(*(r.keys() for r in self.reagents))        miss = tgt - prov        extra = prov - tgt        if miss:            print(f&quot;Error: Missing elements: &#123;miss&#125;&quot;)            exit()        if extra:            print(f&quot;Warning: Extra elements: &#123;extra&#125;&quot;)    def solve_basis(self):        # Construct A, b corresponding to 1 mol         elems = sorted(set(self.target) | set().union(*(r.keys() for r in self.reagents)))        A = [[r.get(e, Decimal(0)) for r in self.reagents] for e in elems]        b = [self.target.get(e, Decimal(0)) for e in elems]        # Convert to rational number to avoid floating point error        A_rat = [[Rational(str(val)) for val in row] for row in A]        b_rat = [Rational(str(val)) for val in b]        vars = symbols(f&#x27;x0:&#123;len(self.reagents)&#125;&#x27;)        sol = linsolve((Matrix(A_rat), Matrix(b_rat)), vars)        if not sol:            print(&quot;Error: No exact solution for 1 mol target.&quot;)            exit()        tup = next(iter(sol))        # Convert rational to decimal exact representation        base = &#123;&#125;        for i, r in enumerate(tup):            base[vars[i]] = Decimal(r.p) / Decimal(r.q)        return base, elems    def calculate(self):        t = input(&quot;Input type (0 mass g, 1 moles): &quot;).strip()        if t not in [&#x27;0&#x27;,&#x27;1&#x27;]:            print(&quot;Invalid type&quot;)            exit()        v = Decimal(input(&quot;Amount: &quot;).strip())        n = v / self.target_mm if t==&#x27;0&#x27; else v        print(f&quot;Target moles = &#123;n.normalize()&#125;&quot;)        base, elems = self.solve_basis()        print(&quot;\nReagent requirements:\n&quot; + &quot;=&quot;*40)        total_mass = Decimal(0)        for i, var in enumerate(sorted(base.keys(), key=lambda x: int(str(x)[1:]))):            moles = base[var] * n            mass = moles * self.molar_masses[i]            total_mass += mass            print(f&quot;Reagent &#123;i+1&#125; (&#123;self.reagent_formulas[i]&#125;):&quot;)            print(f&quot;  Moles: &#123;moles.normalize():.10f&#125; mol&quot;)            print(f&quot;  Mass:  &#123;mass.normalize():.10f&#125; g\n&quot;)        print(f&quot;Total mass = &#123;total_mass.normalize():.10f&#125; g&quot;)        #Verify element balance        print(&quot;Verification:&quot;)        for e in elems:            actual = sum((base[symbols(f&#x27;x&#123;i&#125;&#x27;)] * n) * r.get(e, Decimal(0))                         for i, r in enumerate(self.reagents))            target_amt = self.target.get(e, Decimal(0)) * n            print(f&quot;&#123;e&#125;: target &#123;target_amt.normalize():.10f&#125;, actual &#123;actual.normalize():.10f&#125;&quot;)def main():    print(&quot;High-Precision Compound Synthesis Calculator&quot;)    print(&quot;=&quot;*60)    try:        masses = AtomicMassReader.read()        print(f&quot;Loaded elements: &#123;&#x27;, &#x27;.join(masses.keys())&#125;&quot;)    except Exception as e:        print(e)        exit()    calc = SynthesisCalculator(masses)    calc.input_target()    calc.input_reagents()    calc.validate()    calc.calculate()if __name__ == &quot;__main__&quot;:    main()
